using robotManager.Helpful;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using wManager.Wow.Bot.Tasks;
using wManager.Wow.Class;
using wManager.Wow.Helpers;
using wManager.Wow.ObjectManager;
using wManager.Wow.Enums;
#endif

public class FireAtWillUsingBattery : QuestClass
{
	List<int> _mobs = new List<int>()
	{
		122833
	};
	public FireAtWillUsingBattery()
	{
		Name = "Fire At Will Using Battery";
		QuestId.Add(47508);
		Step.AddRange(new[] { 30, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1) && !ObjectManager.Me.PlayerUsingVehicle;
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			var p = new Vector3(856.1496, 2577.846, 386.4698);
			return GoToTask.ToPositionAndIntecractWithNpc(p, 122794, 1);
		}
		if (Questing.ObjectiveComplete(this, 1))
		{
			Lua.LuaDoString("CastPetAction(6);");
			Thread.Sleep(Others.Random(10000, 15000));
			return true;
		}
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_mobs));
		if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable && mob.GetDistance < 100)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Lua.LuaDoString("CastPetAction(1);");
			Thread.Sleep(Usefuls.Latency);
			ClickOnTerrain.Pulse(mob.Position);
			Thread.Sleep(Others.Random(1500, 2000));
		}
		return true;
	}
}

