public class FireAtWillTurnIn : QuestClass
{
	public FireAtWillTurnIn()
	{
		Name = "Fire At Will Turn In";
		QuestId.Add(47508);
		Step.AddRange(new[] { 30, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.Complete(this);
	}
	public override bool Pulse()
	{
		var p = new Vector3(874.6233, 2590.823, 391.3296, "None");
		Questing.TurnIn(p, 121578);
		return true;
	}
}

