public sealed class AnOfferingofLight : QuestUseItemOnClass
{
    public AnOfferingofLight() // Don't forget to use good classe name
    {
		Name = "An Offering of Light";

        QuestId.Add (48559);

        Step.AddRange(new[] { 1, 0, 0, 0 }); // Objective required count
        HotSpots.Add(new Vector3(409.9177f, 1426.398f, 741.7526f));
        ItemId = 152593; // Entry id of item at use
        Range = 5 ; // Range of item
        EntryIdTarget.Add(126700); // Entry id of game objects or creatures/npc whose you need use item on it
        Thread.Sleep(8000);
    }
}

