[Serializable]
public class GMPAuthSettings : robotManager.Helpful.Settings
{
    [Setting]
    [Category("__IMPORTANT__")]
    [DisplayName("Rocketr ORDER ID")]
    [Description("You can find this ID at the top of the Email that you have received after your purchase.")]
    public string TransactionId { get; set; }

    public GMPAuthSettings()
    {
        TransactionId = null;
    }

    public static GMPAuthSettings CurrentSetting { get; set; }

    public bool Save()
    {
        try
        {
            return Save(AdviserFilePathAndName("GMPAuthSettings", "Vanilla"));
        }
        catch (Exception e)
        {
            robotManager.Helpful.Logging.Write("GMPAuthSettings > Save(): " + e);
            return false;
        }
    }

    public static bool Load()
    {
        try
        {
            if (File.Exists(AdviserFilePathAndName("GMPAuthSettings", "Vanilla")))
            {
                CurrentSetting =
                    Load<GMPAuthSettings>(AdviserFilePathAndName("GMPAuthSettings", "Vanilla"));
                return true;
            }
            CurrentSetting = new GMPAuthSettings();
        }
        catch (Exception e)
        {
            robotManager.Helpful.Logging.Write("GMPAuthSettings > Load(): " + e);
        }
        return false;
    }
}

[XmlRoot("GMPVanillaDemoSettings")]
[Serializable] 
public class GMPVanillaDemoSettingsQuester : Settings
{
    public GMPVanillaDemoSettingsQuester()
    {


        settingSelling = true;

        farmCopperOre = 0;
        farmTinOre = 0;
        farmPeacebloom = 0;
        farmSilverleaf = 0;
        farmEarthroot = 0;
        farmRuinedLeatherScraps = 0;
        farmLightLeather = 0;
        farmLinenCloth = 0;
        farmWoolCloth = 0;
        farmRawGold30 = 0;















    }

    public static GMPVanillaDemoSettingsQuester CurrentSettings { get; set; }

    public bool Save()
    {
        try
        {
            return Save(AdviserFilePathAndName("GMPVanillaDemo", ObjectManager.Me.Name + "." + Usefuls.RealmName));
        }
        catch (Exception e)
        {
            Logging.WriteDebug("GMPVanillaDemoSettingsQuester => Save(): " + e);
            return false;
        }
    }

    public static bool Load()
    {
        try
        {
            if (File.Exists(AdviserFilePathAndName("GMPVanillaDemo", ObjectManager.Me.Name + "." + Usefuls.RealmName)))
            {
                CurrentSettings =
                    Load<GMPVanillaDemoSettingsQuester>(AdviserFilePathAndName("GMPVanillaDemo",
                                                                    ObjectManager.Me.Name + "." + Usefuls.RealmName));
                return true;
            }
            CurrentSettings = new GMPVanillaDemoSettingsQuester();
        }
        catch (Exception e)
        {
            Logging.WriteDebug("GMPVanillaDemo => Load(): " + e);
        }
        return false;
    }





