// Settings:
		var zeppelinEntryId = 183177;


		// From
		var fromZeppelinWaitPosition = new Vector3(283.5827, 5935.404, 149.3924);
		var fromPlayerWaitPosition = new Vector3(285.7265, 5952.735, 149.7803, "None");
		var fromPlayerInZeppelinPosition = new Vector3(289.4869, 5940.366, 149.4167, "None");


		// To
		var toZeppelinWaitPosition = new Vector3(285.749, 5918.21, 26.1411);
		var toPlayerLeavePosition =  new Vector3(286.3895, 5934.434, 26.58661, "None");
		

		if (Usefuls.ContinentId == (int)ContinentId.Expansion01)
		{
			if (!ObjectManager.Me.InTransport)
			{
				if (wManager.Wow.Bot.Tasks.GoToTask.ToPosition(fromPlayerWaitPosition))
				{
					var zeppelin = ObjectManager.GetWoWGameObjectByEntry(zeppelinEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();

		while (zeppelin.Position.DistanceTo(fromZeppelinWaitPosition) > 1) 
		{
		 Thread.Sleep(300);
		 }

			if (zeppelin != null && zeppelin.Position.DistanceTo(fromZeppelinWaitPosition) <= 1)
					{
                        wManager.Wow.Bot.Tasks.GoToTask.ToPosition(fromPlayerInZeppelinPosition);
					}
					
					while(!(zeppelin.Position.DistanceTo(toZeppelinWaitPosition) <= 1))
					
					{
					Thread.Sleep(500);
					}
					
					wManager.Wow.Bot.Tasks.GoToTask.ToPosition(toPlayerLeavePosition);
				}
			}
		}

