local questId = 10394;

local i = 1
while GetQuestLogTitle(i) do
  local questTitle, level, questTag, suggestedGroup, isHeader, isCollapsed, isComplete, isDaily = GetQuestLogTitle(i);
  if ( not isHeader and GetQuestLink(i))  then
      if questId == tonumber(string.match(GetQuestLink(i), 'Hquest:(%d+)')) then
        SelectQuestLogEntry(i);
        SetAbandonQuest();
        AbandonQuest();
        break;
      end
  end
  i = i + 1
end

int questId = 9361; // http://www.wowhead.com/quest=9361/helboar-the-other-white-meat
if (wManager.Wow.Helpers.Quest.HasQuest(questId))
{
    wManager.Events.LootingEvents.OnLootSuccessful += unit =>
    {
        if (wManager.Wow.Helpers.Quest.HasQuest(questId))
        {
            if (wManager.Wow.Helpers.Conditions.InGameAndConnectedAndAliveAndProductStarted &&
                !wManager.Wow.Helpers.Conditions.IsAttackedAndCannotIgnore)
            {
                if (wManager.Wow.Helpers.ItemsManager.GetItemCountById(23270) > 0) // Purified Helboar Meat
                {
                    wManager.Wow.Helpers.ItemsManager.UseItem(23268); // Purification Mixture
                    wManager.Wow.Helpers.Usefuls.WaitIsCasting();
                }
            }
        }
    };
}

Thread t = new Thread(() =>
{
 uint itemId = 29513;
 int questId = 10369;
 while (robotManager.Products.Products.IsStarted)
 {
  if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
  {
   if (!Quest.HasQuest(questId))
    break;
   if (ObjectManager.Target.IsValid && ObjectManager.Target.IsAlive && ObjectManager.Target.HealthPercent <= 100)
   {
    ItemsManager.UseItem(itemId);
   }
  }
  Thread.Sleep(5000);
 }
});
t.Start();

