// Settings:
  var zeppelinEntryId = 152614;


  // From
  var fromZeppelinWaitPosition = new Vector3(4183.809, 5281.077, 26.65743);
  var fromPlayerWaitPosition = new Vector3(4180.805, 5281.014, 26.6934, "None");
  var fromPlayerInZeppelinPosition = new Vector3(4183.719, 5281.316, 26.7378, "None");


  // To
  var toZeppelinWaitPosition = new Vector3(4184.28, 5281.59, 39.4847);
  var toPlayerLeavePosition =  new Vector3(4177.459, 5282.11, 39.60874, "None");
  

  if (Usefuls.ContinentId == (int)ContinentId.Northrend)
  {
   if (!ObjectManager.Me.InTransport)
   {
    if (wManager.Wow.Bot.Tasks.GoToTask.ToPosition(fromPlayerWaitPosition))
    {
     var zeppelin = ObjectManager.GetWoWGameObjectByEntry(zeppelinEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();

  while (zeppelin.Position.DistanceTo(fromZeppelinWaitPosition) > 1) 
  {
   Thread.Sleep(300);
   }

   if (zeppelin != null && zeppelin.Position.DistanceTo(fromZeppelinWaitPosition) <= 1)
     {
      wManager.Wow.Helpers.Move.Forward(Move.MoveAction.PressKey,700);
     }
     
     while(!(zeppelin.Position.DistanceTo(toZeppelinWaitPosition) <= 1))
     
     {
     Thread.Sleep(500);
     }
     
     wManager.Wow.Bot.Tasks.GoToTask.ToPosition(toPlayerLeavePosition);
    }
   }
  }

