for i=1,GetNumGossipOptions() do local _,n=GetGossipOptions(); if n == 'trainer' then SelectGossipOption(i) end end
C_Timer.After(1, function()
	SetTrainerServiceTypeFilter('available',1);
	SetTrainerServiceTypeFilter('unavailable',0);
	SetTrainerServiceTypeFilter('used',0);
end)
C_Timer.After(2, function()
	for i=0,GetNumTrainerServices(),1 do 
		C_Timer.After(0.1 + i, function() BuyTrainerService(1) end)
	end
end)

