/* SUMMON HELPERS EXTRA BUTTON */
  Thread t = new Thread(() =>
  {
   while (robotManager.Products.Products.IsStarted)
   {
    if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
    {
     if (!Questing.Has(39776, 39862))
      break;

     var me = ObjectManager.Me;
     var targ = ObjectManager.Me.TargetObject;
     if (me.IsValid && me.IsAlive && targ.IsValid && targ.IsAlive && (targ.Entry == 99217 || targ.Entry == 99216 || targ.Entry == 97013) )
     {
      Questing.UseExtraButton();
      Thread.Sleep(5000);
     }
    }
    Thread.Sleep(1000);
   }
  });
  t.Start();

