	List<Vector3> pathFrom1;
	List<Vector3> pathFrom2;
	List<Vector3> pathFrom3;
	int _objectiveReturn = 1;
	public EvacuateSnowmane()
	{
		Name = "Evacuate Snowmane";
		QuestId.Add(42088);
		Step.AddRange(new[] { 1, 1, 1});
		pathFrom1 = new List<Vector3>(pathTo1);
		pathFrom1.Reverse();

		pathFrom2 = new List<Vector3>(pathTo2);
		pathFrom2.Reverse();

		pathFrom3 = new List<Vector3>(pathTo3);
		pathFrom3.Reverse();

		_start = pathTo1[0];
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name +"] " + text);
	}
	bool DoObjective(int objNum, List<Vector3> pathTo, List<Vector3> pathFrom, int npcID)
	{
		if (_objectiveReturn != objNum)
			return false;

		var minDist = 5f;
		var myPos = ObjectManager.Me.Position;
		if (!Questing.ObjectiveComplete(this, objNum))
		{
			//do objective
			if (myPos.DistanceTo(pathTo[pathTo.Count - 1]) < minDist)
			{
				Log("near obj#" + objNum+" npc pos. should interact ");
				var npc = Questing.FindUnit(npcID);
				if (npc != null && npc.IsValid)
				{
					GoToTask.ToPositionAndIntecractWithNpc(npc.Position, npc.Entry, 1);
				}
			}
			else
			{
				Log("to obj#" + objNum + " ");
				Questing.PathFromNear(pathTo);
			}
			return true;
		}
		else if (myPos.DistanceTo(_start) < 5)
		{
			Log("returned from obj#" + objNum + " ");
			_objectiveReturn += 1;
			return true;
		}
		else
		{
			Log("from obj#" + objNum + " ");
			Questing.PathFromNear(pathFrom);
			return true;
		}
	}

	public override bool Pulse()
	{
		Questing.DiableFlying();

		if (DoObjective(1, pathTo1, pathFrom1, 106115))
			return true;

		if (DoObjective(2, pathTo2, pathFrom2, 106153))
			return true;

		if (DoObjective(3, pathTo3, pathFrom3, 106191))
			return true;

		Log("quest done");
		return true;
	}

	public override bool IsComplete()
	{
		return _objectiveReturn > 3;
	}
}

