public class JusticeRainsfromAbove : QuestClass
{
	List<int> _mobs = new List<int>()
	{
		97235,
		97241,
	};
	public JusticeRainsfromAbove()
	{
		Name = "Justice Rains from Above";
		QuestId.Add(40594);
		Step.AddRange(new[] { 1, 1 });
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			var p = new Vector3(3931.498, 3929.8, 905.2335, "None");
			GoToTask.ToPositionAndIntecractWithNpc(p, 100712, 1);
			return true;
		}
		Conditions.ForceIgnoreIsAttacked = true;
		var mob = Questing.FindUnit(_mobs);
		if (mob != null && mob.IsValid && mob.GetDistance < 100)
		{
			Questing.VehicleButton(1);
			ClickOnTerrain.Pulse(mob.Position);
			Usefuls.WaitIsCasting();
		}
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 2);
	}
}

