public class ScoutItOut : QuestClass
{
	public ScoutItOut()
	{
		Name = "Scout It Out";
		QuestId.Add(40228);
		Step.AddRange(new[] { 1 });
	}
	public override bool Pulse()
	{
		var moose = Questing.FindUnit(95762);
		if (moose != null && moose.IsValid && moose.IsAttackable)
		{
			Questing.Attack(moose);
			return true;
		}
		var p = new Vector3(4849.4, 4613.782, 761.7373, "None");
		if (GoToTask.ToPosition(p))
		{
			ItemsManager.UseItem(130074);
		}
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
}
