Thread t = new Thread(() =>
{
 while (robotManager.Products.Products.IsStarted)
 {
  if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
  {
   if (!Questing.Has(46841))
    break;

   var me = ObjectManager.Me;
   var targ = ObjectManager.Me.TargetObject;
   if (me.IsValid && me.IsAlive && targ.IsValid && targ.IsAlive && (targ.Entry == 120602 || targ.Entry == 120598 || targ.Entry == 120601))
   {
    Usefuls.WaitIsCasting();
    Questing.UseExtraButton();
    Thread.Sleep(5000);
   }
  }
  Thread.Sleep(1000);
 }
});
t.Start();

