public class TheHandofFate : QuestClass
{
	public TheHandofFate()
	{
		Name = "The Hand of Fate";
		QuestId.Add(47835);
		QuestId.Add(48507);
		QuestId.Add(47221);
		QuestId.Add(48506);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	public override bool Pulse()
	{
		Traveler.ToCapitalOpen();
		return true;
	}
	public override bool IsComplete()
	{
		if (ObjectManager.Me.IsHorde)
		{
			return Traveler.InKalimdor && !Traveler.InOrgrimmarCleftOfShadow;
		}
		else if (ObjectManager.Me.IsAlliance)
		{
			return Traveler.InEasternKingdoms && !Traveler.InStormwindWizardsSanctum;
		}
		return false;
	}
	public override bool PickUp()
	{
		if (!LegionQuests.InDalaran)
		{
			LegionQuests.UseDalaranHeathstone();
			return true;
		}
		return base.PickUp();
	}
}

