public class ReturntotheVindicaar : QuestClass
{
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(3 * 60 * 1000);
	static int _questID = 46843;
	public ReturntotheVindicaar()
	{
		Name = "Return to the Vindicaar";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	public override bool PickUp()
	{
		var npc = NpcPickUp;
		if (npc != null)
		{
			if (npc.Id == 120738)
				npc.Id = 124995;
			else
				npc.Id = 120738;
		}
		_timer.Reset();
		return base.PickUp();
	}
	public override bool IsComplete()
	{
		return ArgusHelper.InVendicar;
	}
	public override bool Pulse()
	{
		var p = new Vector3(1349.102, 1861.349, 548.2333);
		var gate = Questing.FindObject(272453);
		if (gate != null && gate.IsValid)
		{
			Questing.Gather(p, gate.Entry);
		}
		else if (_timer.IsReady)
		{
			Questing.Abandon(_questID);
			Log("no gate and timer expired. abandon quest");
			Thread.Sleep(Others.Random(3000, 5000));
		}
		wManager.Wow.Bot.States.AntiAfk.Pulse();
		return true;
	}
	void Log(string text)
	{
		Logging.Write("["+Name+"] " + text);
	}
}

