public class BringingtheBigGuns : QuestClass
{
	static int _questID = 47526;
	Vector3 _position = new Vector3(1224.019, 2592.619, 425.6583, "None");
	public BringingtheBigGuns()
	{
		Name = "Bringing the Big Guns";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1 });
	}
	public override bool Pulse()
	{
		UseLightsJudgment();
		return true;
	}
	bool UseLightsJudgment()
	{
		return Questing.Gather(_position, 273658, 4.5f);
	}
	public override bool TurnIn()
	{
		UseLightsJudgment();
		return base.TurnIn();
	}
	public static void StartCombatFix()
	{
		Thread t = new Thread(() =>
		{
			while (robotManager.Products.Products.IsStarted)
			{
				if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
				{
					if (!Quest.HasQuest(_questID))
						break;

					if (ObjectManager.Me.HaveBuff(251163))
					{
						wManager.Wow.Helpers.Keybindings.PressKeybindings(wManager.Wow.Enums.Keybindings.STRAFELEFT, 300);
					}
				}
				Thread.Sleep(500);
			}
		});
		t.Start();
	}
}

