public class AFloatingRuin : QuestClass
{
	public AFloatingRuin()
	{
		Name = "A Floating Ruin";
		QuestId.Add(48081);
		Step.AddRange(new[] { 1 });
	}
	public override bool Pulse()
	{
		if (!ArgusHelper.InVendicarKrokuun)
		{
			ArgusHelper.ToVendicarKrokuun();
			return true;
		}
		ArgusHelper.ToVendicarMacAree();
		return true;
	}
	public override bool IsComplete()
	{
		return ArgusHelper.InVendicarMacAree;
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InVendicarKrokuun)
		{
			ArgusHelper.ToVendicarKrokuun();
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.TurnIn();
	}
}

