public class MacAreeJewelofArgus : QuestClass
{
	public MacAreeJewelofArgus()
	{
		Name = "Mac'Aree, Jewel of Argus";
		QuestId.Add(46815);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	public override bool Pulse()
	{
		if (ToMacAreeOpen())
		{
			var p = new Vector3(4837.398, 9743.249, -65.22211, "None");
			GoToTask.ToPositionAndIntecractWithNpc(p, 125968, 1);
			Questing.CancelCutscene();
		}
		return true;
	}
	public static bool ToMacAreeOpen()
	{
		if (ArgusHelper.InMacAreeOpen)
			return true;

		if (ArgusHelper.InVendicarMacAree)
		{
			var p = new Vector3(4731.171, 9861.155, 64.29391, "None");
			GoToTask.ToPositionAndIntecractWithNpc(p, 121263, 1);
			return false;
		}
		ArgusHelper.ToVendicarMacAree();
		return false;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 2);
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.PickUp();
	}
}

