var varName = "Camelot10_Quest47689";
var points = new List<Vector3>()
{
	new Vector3(5533.183, 9314.478, -66.17178, "None"),
	new Vector3(5535.277, 9280.682, -69.17925, "None"),
	new Vector3(5534.569, 9288.087, -58.10923, "None"),
	new Vector3(5537.209, 9237.159, -55.85361, "None"),
};
var progress = Var.GetVar<int>(varName);
if (progress >= points.Count)
{
	var p0 = new Vector3(5537.474, 9235.034, -56.06606, "None");
	GoToTask.ToPositionAndIntecractWithNpc(p0, 124077, 1);
	Logging.WriteDebug("[Gatekeeper's Challenge: Tenacity] complete challenge");
	return true;
}
var p = points[progress];
if (GoToTask.ToPosition(p))
{
	if (ObjectManager.Me.HealthPercent < 99)
		return true;

	progress += 1;
	Var.SetVar(varName, progress);
	Logging.WriteDebug("[Gatekeeper's Challenge: Tenacity] reached point #" + progress +" wait unitl full hp");
}
return true;

