uint red = 243755; uint blue = 243761; uint yellow = 243762;
var statueRed = 122185; var statueBlue = 122186; var statueYellow = 122187;
var buffs = new List<uint>() { red, blue, yellow };
var statues = new List<int>() { statueRed, statueBlue, statueYellow };
var targets = new List<int>() { 271668 /*cache-of-acuity*/, 271669 /*cache-of-wit*/, 271670 /*cache-of-guile*/, };
var hotspots = new List<Vector3>()
{
	new Vector3(5648.444, 9468.91, -64.40834),
	new Vector3(5693.095, 9532.522, -66.60447),
	new Vector3(5664.814, 9560.17, -66.49979),
};
var checks = new List<bool[]>()
{
	new bool[] { false, true, true },
	new bool[] { true, false, true },
	new bool[] { true, true, false },
};
int obj = 0;
if (Questing.ObjectiveComplete(this, 2))
	obj = 1;
if (Questing.ObjectiveComplete(this, 3))
	obj = 2;

var p0 = new Vector3(5633.602, 9508.799, -68.81554, "None");
var flags = checks[obj];
for (int i = 0; i < flags.Length; i++)
{
	var needBuff = flags[i];
	var buffID = buffs[i];
	if (ObjectManager.Me.HaveBuff(buffID) != needBuff)
	{
		Logging.WriteDebug("[Gatekeeper's Challenge: Cunning] obj#"+ obj+" change buff=" + SpellListManager.SpellNameInGameById(buffID) + " need=" + needBuff);
		var npc = statues[i];
		GoToTask.ToPositionAndIntecractWithNpc(p0, npc);
		Questing.Wait(2);
		return true;
	}
}
var p1 = hotspots[obj];
var targetID = targets[obj];
Logging.WriteDebug("[Gatekeeper's Challenge: Cunning] opened objective #"+ obj + ". get it");
Questing.Gather(p1, targetID);
return true;

