public class TheSigilofAwakening : QuestClass
{
	static List<Vector3> path = new List<Vector3>()
	{
		new Vector3(4701.268f, 9855.859f, 56.15506f, "None"),
		new Vector3(4693.399f, 9853.496f, 56.06574f, "None"),
		new Vector3(4686.556f, 9851.838f, 56.06574f, "None"),
		new Vector3(4679.772f, 9850.196f, 56.0659f, "None"),
		new Vector3(4672.849f, 9848.77f, 56.0659f, "None"),
		new Vector3(4666.056f, 9847.396f, 58.70428f, "None"),
		new Vector3(4659.628f, 9845.885f, 60.08388f, "None"),
	};
	public TheSigilofAwakening()
	{
		Name = "The Sigil of Awakening";
		QuestId.Add(48107);
		Step.AddRange(new[] { 1, 1 });
	}
	public override bool Pulse()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		var p = new Vector3(4659.48, 9846.083, 60.0858, "None");
		var c = new Vector3(4682.927, 9851.674, 56.06613, "None");
		var myP = ObjectManager.Me.Position;
		if (myP.DistanceTo(p) < 7)
		{
			Questing.Gather(p, 272692, 7);
		}
		else if (myP.DistanceTo2D(c) < 25 && myP.DistanceZ(c) < 10)
		{
			Questing.PathFromNear(path);
		}
		else
		{
			GoToTask.ToPosition(path[0]);
		}
		return true;
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InMacAreeOpen)
		{
			ArgusHelper.ToMacAreeOpen();
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.TurnIn();
	}
}
