public class BattleforValsharahInvasion : QuestClass
{
	public BattleforValsharahInvasion()
	{
		Name = "Battle for Val'sharah (Invasion)";
		QuestId.Add(45856);
		Step.AddRange(new[] { 1, 1, 0, 0, 0 });
		RequiredQuest = 45856;
	}
	public override bool Pulse()
	{
		if (ObjectManager.Me.PlayerUsingVehicle || ObjectManager.Me.IsOnTaxi)
			return true;

		if (!HoldingtheRampartsInvasion.InBastion)
		{
			Logging.WriteDebug("[Battle for Val'sharah] goto bastion");
			GoToTask.ToPositionAndIntecractWithNpc(HoldingtheRampartsInvasion.start, 118183, 1);
			return true;
		}
		var p = new Vector3(3104.49, 7177.093, 135.8834, "None");
		if (GoToTask.ToPositionAndIntecractWithNpc(p, 118250, 1))
		{
			if (Questing.Scenario.IsButtonEnterPressed)
			{

			}
			Thread.Sleep(Others.Random(5000, 7000));
			return true;
		}
		return true;
	}
	public override bool IsCompleted()
	{
		if (Quest.HasQuest(45856))
			return false;
		return base.IsCompleted();
	}
	public override bool IsComplete()
	{
		return Quest.IsObjectiveComplete(2, 45856) || (Usefuls.ContinentId == (int)ContinentId.LegionShipHorizontalValsharah && !ObjectManager.Me.PlayerUsingVehicle);
	}
}

