public class HoldingtheRampartsInvasion : QuestClass
{
	static int questID = 44789;
	public static List<Vector3> rampartsPath = new List<Vector3>()
	{
		new Vector3(3138.49f, 7474.39f, 209.296f, "None"),
		new Vector3(3141.328f, 7468.057f, 209.296f, "None"),
		new Vector3(3142.379f, 7464.681f, 209.296f, "None"),
		new Vector3(3144.373f, 7458.015f, 209.296f, "None"),
		new Vector3(3145.897f, 7451.202f, 209.296f, "None"),
		new Vector3(3146.506f, 7447.706f, 209.296f, "None"),
		new Vector3(3148.71f, 7441.233f, 209.296f, "None"),
		new Vector3(3150.302f, 7438.078f, 209.296f, "None"),
		new Vector3(3152.506f, 7432.531f, 209.296f, "None"),
		new Vector3(3154.631f, 7425.93f, 209.296f, "None"),
		new Vector3(3155.298f, 7422.427f, 209.296f, "None"),
		new Vector3(3155.163f, 7415.46f, 209.296f, "None"),
		new Vector3(3155.04f, 7408.506f, 209.296f, "None"),
		new Vector3(3157.223f, 7401.897f, 209.296f, "None"),
		new Vector3(3158.628f, 7397.989f, 209.296f, "None"),
		new Vector3(3159.15f, 7394.464f, 209.296f, "None"),
		new Vector3(3160.571f, 7387.688f, 209.296f, "None"),
		new Vector3(3161.308f, 7384.195f, 209.296f, "None"),
		new Vector3(3163.834f, 7377.844f, 209.296f, "None"),
		new Vector3(3168.226f, 7372.647f, 209.296f, "None"),
		new Vector3(3173.039f, 7370.766f, 209.296f, "None"),
		new Vector3(3179.467f, 7368.425f, 209.296f, "None"),
		new Vector3(3182.952f, 7368.854f, 209.296f, "None"),
		new Vector3(3189.217f, 7370.581f, 209.296f, "None"),
		new Vector3(3192.733f, 7371.197f, 209.296f, "None"),
		new Vector3(3199.662f, 7372.413f, 209.296f, "None"),
		new Vector3(3203.11f, 7373.017f, 209.296f, "None"),
		new Vector3(3210.014f, 7374.163f, 209.296f, "None"),
		new Vector3(3216.975f, 7374.407f, 209.296f, "None"),
		new Vector3(3223.737f, 7372.902f, 209.296f, "None"),
	};
	public static Vector3 rampartsCenter = new Vector3(3153.029, 7400.599, 209.2972, "None");
	public static Vector3 spireCenter = new Vector3(3274.101, 7387.536, 292.173, "None");
	public static Vector3 spireCamp = new Vector3(3277.167, 7356.817, 292.173, "None");
	public static Vector3 bastionCenter = new Vector3(3117.537, 7151.847, 134.3274, "None");
	public static Vector3 start = new Vector3(3068.221, 7451.635, 52.3199);
	public static Vector3 taxi1 = new Vector3(3227.446, 7370.364, 209.2958, "None");
	static float _distZ = 15;
	static bool _objective1Finished = false;
	static bool _objective2Finished = false;
	bool _pathReverse = false;
	public static List<int> mobs2 = new List<int>(){115656, 115657, 115658};
	public static int harvest4 = 115659;
	public static List<int> mobs4 = new List<int>() { 115603 };

	public HoldingtheRampartsInvasion()
	{
		Name = "Holding the Ramparts (Invasion)";
		QuestId.Add(questID);
		Step.AddRange(new[] { 1, 1, 1, 1 });
		RequiredQuest = 45812; // http://www.wowhead.com/quest=45812/assault-on-valsharah
	}
	public override bool Pulse()
	{
		if (ObjectManager.Me.PlayerUsingVehicle || ObjectManager.Me.IsOnTaxi)
			return true;

		if (DoObjective2())
			return true;

		if (DoObjective4())
			return true;

		ToBastion();
		return true;
	}
	public override bool TurnIn()
	{
		if (!InBastion)
		{
			ToBastion();
			return true;
		}
		return base.TurnIn();
	}
	public static bool ToBastion()
	{
		if (InBastion)
			return true;

		if (InSpire)
		{
			Log("#5 use taxi2");
			GoToTask.ToPositionAndIntecractWithNpc(spireCamp, 118405, 1);
			return true;
		}
		if (InRamparts)
		{
			if (ObjectManager.Me.Position.DistanceTo2D(taxi1) > 15)
			{
				Log("#5 to end of ramparts");
				Questing.PathFromNear(rampartsPath, false);
				return true;
			}
			Log("#5 goto spire");
			GoToTask.ToPositionAndIntecractWithNpc(taxi1, 118358, 1);
			return true;
		}
		Log("#5 goto ramparts");
		GoToTask.ToPositionAndIntecractWithNpc(start, 118183, 1);
		return true;
	}
	bool DoObjective4()
	{
		var myPos = ObjectManager.Me.Position;
		if (Questing.ObjectiveComplete(this, 3) && Questing.ObjectiveComplete(this, 4))
			return false;

		if (InSpire)
		{
			var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs4));
			if (mob != null && mob.IsValid && mob.HealthPercent < 100)
			{
				Log("#4 attack " + mob.Name);
				Interact.InteractGameObject(mob.GetBaseAddress);
				Fight.StartFight(mob.Guid);
				return true;
			}
			var harvester = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(harvest4));
			if (harvester != null && harvester.IsValid && harvester.IsAlive)
			{
				Log("#4 use " + harvester.Name);
				GoToTask.ToPositionAndIntecractWithNpc(harvester.Position, harvester.Entry);
				return true;
			}
			GoToTask.ToPosition(spireCamp);
			return true;
		}
		if (InRamparts)
		{
			if (myPos.DistanceTo2D(taxi1) > 15)
			{
				Log("#4 to end of ramparts");
				Questing.PathFromNear(rampartsPath, false);
				return true;
			}
			Log("#4 goto spire");
			GoToTask.ToPositionAndIntecractWithNpc(taxi1, 118358, 1);
			return true;
		}
		Log("#4 goto ramparts");
		GoToTask.ToPositionAndIntecractWithNpc(start, 118183, 1);
		return true;
	}

	bool DoObjective2()
	{
		var myPos = ObjectManager.Me.Position;
		if (!_objective1Finished)
		{
			_objective1Finished = Questing.ObjectiveComplete(this, 2);
			Log("#1 finished=" + _objective1Finished);
		}
		if (!_objective2Finished)
		{
			_objective2Finished = Questing.ObjectiveComplete(this, 2);
			Log("#2 finished=" + _objective2Finished);
		}

		if (_objective1Finished && _objective2Finished)
			return false;

		if (!InRamparts)
		{
			Log("#2 goto ramparts");
			GoToTask.ToPositionAndIntecractWithNpc(start, 118183, 1);
			return true;
		}
		var mob = FindMob(mobs2);
		if (mob != null)
		{
			Log("#2 attack " + mob.Name);
			Questing.Attack(mob);
			return true;
		}
		if (_pathReverse && myPos.DistanceTo2D(rampartsPath[0]) < 20)
		{
			Log("#2 patrol forward");
			_pathReverse = false;
		}
		else if (!_pathReverse && myPos.DistanceTo2D(rampartsPath[rampartsPath.Count - 1]) < 20)
		{
			Log("#2 patrol back");
			_pathReverse = true;
		}
		var path = new List<Vector3>(rampartsPath);
		if (_pathReverse)
			path.Reverse();

		Questing.PathFromNear(path, false, () => { return FindMob(mobs2) == null; });
		return true;
	}

	WoWUnit FindMob(List<int> mobs)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
		if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable && mob.GetDistance < 25)
			return mob;
		return null;
	}
	public override bool IsComplete()
	{
		if (!InBastion)
			return false;

		return Questing.ObjectiveComplete(this, 1) && Questing.ObjectiveComplete(this, 2) && Questing.ObjectiveComplete(this, 3) && Questing.ObjectiveComplete(this, 4);
	}

	public static bool InRamparts
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceTo2D(rampartsCenter) < 100 && myPos.DistanceZ(rampartsCenter) < _distZ;
		}
	}
	public static bool InSpire
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceTo2D(spireCenter) < 70 && myPos.DistanceZ(spireCenter) < _distZ;
		}
	}
	public static bool InBastion
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceTo2D(bastionCenter) < 40 && myPos.DistanceZ(bastionCenter) < _distZ;
		}
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Holding the Ramparts] " + text);
	}
