public class LeaveBlackRookHoldRamparts : QuestClass
{
	Vector3 _center = new Vector3(3184.154, 7414.437, 209.2954, "None");
	Vector3 _center2 = new Vector3(3116.408, 7141.245, 134.327, "None");

	public LeaveBlackRookHoldRamparts()
	{
		Name = "Leave Black Rook Hold Ramparts";
		QuestId.Add(0);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		WorldQuest.Whistle.Use();
		return true;
	}
	public override bool IsComplete()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
			return false;

		if (ObjectManager.Me.Position.DistanceTo(_center) < 200)
			return ObjectManager.Me.Position.Z > (_center.Z - 10);

		if (ObjectManager.Me.Position.DistanceTo(_center2) < 50)
			return ObjectManager.Me.Position.Z > (_center.Z - 10);

		return true;
	}
	public override bool HasQuest()
	{
		return true;
	}

}

