QuestClass.Code:
if (SuramarQuests.GatherAncientMana.NotCollected(1200))
	return true;

var npc = Questing.FindUnit(NpcTurnIn.Id);
if (npc != null && npc.IsValid)
{
	GoToTask.ToPositionAndIntecractWithNpc(npc.Position, npc.Entry);
	Questing.Gossip(3);
	Questing.ClickStaticPopup(1, 1);
}
else
{
	GoToTask.ToPosition(NpcTurnIn.Position);
}
return true;

var progress = Questing.ObjectiveProgress(this, 1);
if (progress == 0)
{
	Logging.WriteDebug("[Rumor Has It] #1");
	var p = new Vector3(1021.601, 3397.102, 21.58341);
	GoToTask.ToPositionAndIntecractWithNpc(p, 110875, 1);
}
else if (progress == 1)
{
	Logging.WriteDebug("[Rumor Has It] #2");
	var p = new Vector3(1128.737, 3207.99, 20.00055);
	GoToTask.ToPositionAndIntecractWithNpc(p, 110876, 1);
}
else
{
	Logging.WriteDebug("[Rumor Has It] #3");
	var p = new Vector3(1291.73, 3369.066, 21.49526);
	GoToTask.ToPositionAndIntecractWithNpc(p, 110874, 1);
}
return true;

//	robotManager.Helpful.Var.SetVar("Camelot10SuppressWarning", true);
bool isComplete = true;
foreach (var questID in QuestId)
{
	if (Quest.HasQuest(questID) && !Quest.IsObjectiveComplete(1, questID))
		isComplete = false;
}
if (MinLevel < 1 && !isComplete && !Var.GetVar<bool>("Camelot10SuppressWarning"))
{
	Logging.Write("WARNING! ATTENTION! Next two quests is hard to script to avoid detectors. You can quickly do this by hands. Profile paused and resume after 30 seconds. Pause and complete objectives for 'Rumor Has It' and 'Thinly Veiled Threats'.");
	Questing.Wait(30);
}
MinLevel = 1;
return true;

