public class LylethsChampion : QuestClass
{
	Vector3 _startPosition = new Vector3(1141.786, 4256.675, 21.32814, "None");
	Vector3 _targetPosition = new Vector3(1111.839, 4206.534, 179.352);
	int _bossID = 110354;   // http://www.wowhead.com/npc=110354/coryn
	int _vehicleID = 110681;
	uint _spellForceBlast = 220067; //1
	uint _spellSpiralSlice = 220148; //2
	uint _spellSpellbreak = 220081; //3
	uint _spellRewind = 220207; //4
	float _minDist = 4.5f;
	public LylethsChampion()
	{
		Name = "Ly'leth's Champion";
		QuestId.Add(43318);
		Step.AddRange(new[] { 0, 1, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			Questing.CloseGossip();
			Questing.CloseQuest();
			Log("become champion");
			if (GoToTask.ToPositionAndIntecractWithNpc(_startPosition, 107632, 1, false, (c) => {
				if (ObjectManager.Me.PlayerUsingVehicle)
				{
					Log("im a champion");
					return false;
				}
				return true;
			}))
			{
				Questing.Wait(5);
			}
			return true;
		}
		if (Combat())
			return true;

		var boss = Questing.FindUnit(_bossID);
		if (boss != null && boss.IsValid && boss.IsAlive)
		{
			Log("attack boss");
			Interact.InteractGameObject(boss.GetBaseAddress);
			return true;
		}
		Log("wait boss");
		GoToTask.ToPosition(_targetPosition);
		return true;
	}
	bool Combat()
	{
		Conditions.ForceIgnoreIsAttacked = true;
		var target = ObjectManager.GetUnitAttackPlayer().OrderBy(u => u.GetDistance).FirstOrDefault();
		if (target == null)
			target = ObjectManager.Me.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;

		Interact.InteractGameObject(target.GetBaseAddress, true, false, true);
		var dist = ObjectManager.Me.Position.DistanceTo(target.Position);
		if (target.GetDistance > _minDist)
		{
			GoToTask.ToPosition(target.Position, _minDist);
			return true;
		}
		var champion = Questing.FindUnit(_vehicleID);
		MovementManager.StopMove();
		Thread.Sleep(100);
		MovementManager.Face(target);
		Thread.Sleep(100);
		if (target.IsCast && target.CanInterruptCasting && target.CastingTimeLeft < 1000 && Questing.NoCooldownSpell(_spellSpellbreak))
		{
			Log("spellbreak");
			Questing.VehicleButton(3);
			Questing.WaitIsCasting(champion);
			return true;
		}
		if (champion.HealthPercent < 50 && Questing.NoCooldownSpell(_spellRewind))
		{
			Log("rewind");
			Questing.VehicleButton(4);
			Questing.WaitIsCasting(champion);
			return true;
		}
		if (Questing.NoCooldownSpell(_spellForceBlast))
		{
			Log("force blast");
			Questing.VehicleButton(1);
			Questing.WaitIsCasting(champion);
			return true;
		}
		if (Questing.NoCooldownSpell(_spellSpiralSlice))
		{
			Log("spiral slice");
			Questing.VehicleButton(2);
			Questing.WaitIsCasting(champion);
			return true;
		}
		return false;
	}
	public override bool TurnIn()
	{
		wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
		Questing.CloseNPCFrame();
		return base.TurnIn();
	}
	void Log(string text)
	{
		Logging.WriteDebug("[Ly'leth's Champion] " + text);
	}
}

