public class GivingItAllWeveGot : QuestClass
{
	public GivingItAllWeveGot()
	{
		Name = "Giving It All We've Got";
		QuestId.Add(43562);
		Step.AddRange(new[] { 1, 1, 1, 1, 0 });
	}
	public override bool IsComplete()
	{
		for (var i = 1; i <= 8; i++)
		{
			if (!Questing.ObjectiveComplete(this, i))
				return false;
		}
		return true;
	}
	public override bool Pulse()
	{
		var p = new Vector3(1737.695, 4603.249, 96.28294);
		GoToTask.ToPositionAndIntecractWithNpc(p, 103155, 1);
		return true;
	}
	public override bool PickUp()
	{
		if (SuramarQuests.Thirst.Valtrois())
		{
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (SuramarQuests.Thirst.Valtrois())
		{
			return true;
		}
		return base.TurnIn();
	}
}

