public class FlowControl : QuestClass
{
	public FlowControl()
	{
		Name = "Flow Control";
		QuestId.Add(43564);
		Step.AddRange(new[] { 1, 1, 1, 0, 0 });
	}
	public override bool Pulse()
	{
		Conditions.ForceIgnoreIsAttacked = true;
		if (SuramarQuests.InShalaran)
		{
			SuramarQuests.Portals.ShalanarToWaningCrescent.Use();
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 1))
		{
			var p = new Vector3(626.1741, 4118.832, 1.493733);
			Questing.Gather(p, 252776);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			var p = new Vector3(1125.279, 3893.982, 1.305297);
			Questing.Gather(p, 252779);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			var p = new Vector3(1248.596, 3331.287, 20.1206);
			Questing.Gather(p, 252783);
			return true;
		}
		return true;
	}
	public override bool PickUp()
	{
		if (SuramarQuests.Thirst.Valtrois())
		{
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (SuramarQuests.Thirst.Valtrois())
		{
			return true;
		}
		return base.TurnIn();
	}
}

