public class BringHometheBeacon : QuestClass
{
	public BringHometheBeacon()
	{
		Name = "Bring Home the Beacon";
		QuestId.Add(43565);
		Step.AddRange(new[] { 100, 100, 100, 0, 0 });
	}
	public override bool Pulse()
	{
		if (SuramarQuests.InShalaran)
		{
			SuramarQuests.Portals.ShalanarToWaningCrescent.Use();
			return true;
		}
		if (TryCompleteObjective(new Vector3(659.0435, 3924.568, 5.685922), 1))
			return true;
		if (TryCompleteObjective(new Vector3(624.675, 3917.662, 5.685746), 2))
			return true;
		if (TryCompleteObjective(new Vector3(357.1834, 3835.144, 1.493718), 3))
			return true;
		return true;
	}
	bool TryCompleteObjective(Vector3 p, int objNum)
	{
		if (Questing.ObjectiveComplete(this, objNum))
			return false;

		if (GoToTask.ToPosition(p))
		{
			ItemsManager.UseItem(140257);
			Usefuls.WaitIsCasting();
			return true;
		}
		return false;
	}
	public override bool PickUp()
	{
		if (SuramarQuests.Thirst.Oculeth())
		{
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (SuramarQuests.Thirst.Oculeth())
		{
			return true;
		}
		return base.TurnIn();
	}
}

