public class AllIn : QuestClass
{
	public AllIn()
	{
		Name = "All In";
		QuestId.Add(43567);
		Step.AddRange(new[] { 1, 1, 1, 1, 0 });
	}
	public override bool PickUp()
	{
		if (SuramarQuests.Thirst.Thalyssra())
			return true;

		return base.PickUp();
	}
	public override bool Pulse()
	{
		if (RunScenario())
			return true;

		if (!Questing.ObjectiveComplete(this, 2))
		{
			var p = new Vector3(1665, 4734.806, 138.9329);
			GoToTask.ToPositionAndIntecractWithNpc(p, 113062);
			return true;
		}
		var pEnd = new Vector3(1717.735, 4623.107, 123.9469);
		GoToTask.ToPosition(pEnd);
		return true;
	}

	bool RunScenario()
	{
		if (Questing.ObjectiveComplete(this, 1))
			return false;

		switch (Questing.Scenario.Stage)
		{
			case 5:
				var p5 = new Vector3(3144.727, 4884.331, 617.7654);
				break;
			case 4:
				var p4 = new Vector3(3122.951, 4899.215, 617.7654);
				GoToTask.ToPositionAndIntecractWithNpc(p4, 113426);
				break;
			case 3:
			case 2:
				var p2 = new Vector3(3213.082, 4920.253, 619.2164);
				GoToTask.ToPosition(p2);
				break;
			case 1:
				var hotspots = new List<Vector3>() {
					new Vector3(3168.986, 5074.731, 623.2028),
					new Vector3(3199.137, 4980.621, 624.0922),
				};
				var mobs = new List<int>()
				{
					113191,
				};
				Questing.Grind(mobs, hotspots);
				break;
			case 0:
			default:
				var p0 = new Vector3(1719.892, 4596.684, 96.28247, "None");
				if (Usefuls.ContinentId != (int)ContinentId.TheArcwaySuramarScenario)
					GoToTask.ToPositionAndIntecractWithNpc(p0, 98548, 1);

				break;
		}
		return true;
	}


}

