public class ArcandorGiftoftheAncientMagi : QuestClass
{
	public ArcandorGiftoftheAncientMagi()
	{
		Name = "Arcan'dor, Gift of the Ancient Magi";
		QuestId.Add(43568);
		Step.AddRange(new[] { 1, 1, 1, 3, 0 });
	}
	public override bool Pulse()
	{
		var p = new Vector3(1714.576, 4636.15, 123.971, "None");
		if (!SuramarQuests.InShalaran)
		{
			GoToTask.ToPosition(p);
			return true;
		}
		var fruit = ObjectManager.GetObjectWoWGameObject().Where(o => o.Entry == 253081 && o.GetDynamicFlags == -64988).OrderBy(o => o.GetDistance).FirstOrDefault();
		if (fruit != null && fruit.IsValid)
		{
			Questing.Gather(fruit.Position, fruit.Entry);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 1))
		{
			if (GoToTask.ToPositionAndIntecractWithNpc(p, 112147, 1))
			{
				Thread.Sleep(Others.Random(18000, 20000));
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			if (GoToTask.ToPositionAndIntecractWithNpc(p, 112145, 1))
			{
				Thread.Sleep(Others.Random(18000, 20000));
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			if (GoToTask.ToPositionAndIntecractWithNpc(p, 112146, 1))
			{
				Thread.Sleep(Others.Random(18000, 20000));
			}
			return true;
		}
		return true;
	}
}
