	uint _MarkOfTheSentinaxID = 147582;
	string _varName = "Camelot10_MarkOfTheSentinaxNeed";
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(30 * 1000);
	//
	public MarkoftheSentinax()
	{
		Name = "Mark of the Sentinax";
		RequiredQuest = 46251; // http://www.wowhead.com/quest=46251/shard-times
		QuestId.Add(47139);
		EntryTarget.AddRange(_mobs);
		Step.AddRange(new[] { 100 });
		_timer.Reset();
	}
	public override bool Pulse()
	{
		CorrectHotspots();
		return base.Pulse();
	}
	public override bool IsComplete()
	{
		if (Var.Exist(_varName))
		{
			var need = Var.GetVar<int>(_varName);
			// unlimited
			if (need < 0)
				return false;

			var have = ItemsManager.GetItemCountById(_MarkOfTheSentinaxID);
			if (have < need)
				return false;

			Var.SetVar(_varName, 0);
			Var.VarDictionary.Remove(_varName);
			return true;
		}
		return Questing.ObjectiveComplete(this, 1);
	}
	public override bool HasQuest()
	{
		// for limited or unlimited farm
		if (Var.Exist(_varName))
		{
			var need = Var.GetVar<int>(_varName);
			if (need != 0)
				return true;
		}
		return base.HasQuest();
	}
	void CorrectHotspots()
	{
		if (!_timer.IsReady)
			return;

		_timer.Reset();

		var sentinaxPosition = GetSentinaxPosition();
		if (sentinaxPosition == Vector3.Zero)
			return;

		if (HotSpots.Count < 1 || sentinaxPosition.DistanceTo2D(HotSpots[0]) > 50 )
		{
			HotSpots.Clear();
			HotSpots.Add(sentinaxPosition);
			Logging.Write("Correct Sentinax position to:" + sentinaxPosition);
		}
	}
	Vector3 GetSentinaxPosition()
	{
		var p = new Vector3();
		var lua = @"
SetMapByID(1021);
local numPOIs = GetNumMapLandmarks();
for i=1, numPOIs do
	local landmarkType, name, description, textureIndex, x, y, mapLinkID, inBattleMap, graveyardID, areaID, poiID, isObjectIcon, atlasIcon, displayAsBanner = C_WorldMap.GetMapLandmarkInfo(i);
	if (atlasIcon and atlasIcon == 'DemonShip') then
		return x.. '#LUASEPARATOR#'..y
	end
end
return '0#LUASEPARATOR#0'
";
		lua = lua.Replace("#LUASEPARATOR#", Lua.ListSeparator);
		var result = Lua.LuaDoString<List<float>>(lua);
		if (result != null && result.Count == 2)
		{
			p.X = ConvertX(result[0]);
			p.Y = ConvertY(result[1]);
			p.Z = 200;
		}
		return p;
	}

	#region LUA MAP COORDINATES TO WORLD VECTOR3

	Vector3 _pos1Lua = new Vector3(0.44138288497925f, 0.63286304473877f, 0f);
	Vector3 _pos1 = new Vector3(-1628.515f, 3217.342f, 0f, "None");
	Vector3 _pos2Lua = new Vector3(0.44859743118286f, 0.62364858388901f, 0f);
	Vector3 _pos2 = new Vector3(-1604.865, 3189.566, 0, "None");

	public float ConvertX(float luaX)
	{
		return _pos1.X + ((luaX - _pos1Lua.X) * xRatio);
	}
	public float ConvertY(float luaY)
	{
		return _pos1.Y + ((luaY - _pos1Lua.Y) * yRatio);
	}
	float _xRatio = 0f;
	float xRatio
	{
		get
		{
			if (_xRatio == 0)
				_xRatio = (_pos2.X - _pos1.X / (_pos2Lua.X - _pos1Lua.X));

			return _xRatio;
		}
	}
	float _yRatio = 0f;
	float yRatio
	{
		get
		{
			if (_yRatio == 0)
				_yRatio = (_pos2.Y - _pos1.Y / (_pos2Lua.Y - _pos1Lua.Y));

			return _yRatio;
		}
	}
	#endregion LUA MAP COORDINATES TO WORLD VECTOR3
}

		var p = new Vector3(-1621.912, 3202.07, 129.0166, "None");
		GoToTask.ToPositionAndIntecractWithNpc(p, 119305);
		Thread.Sleep(Others.Random(1000, 2000));
