var questId = 44847;
var portsPositions = new List<Vector3>(){
	new Vector3(), //0
	new Vector3(), //1
	new Vector3(1128.371, 6105.732, 84.64314, "None"), //2 - S
	new Vector3(1181.959, 6151.853, 84.67382, "None"), //3 - C
	new Vector3(1285.552, 6126.456, 87.68211, "None"), //4 - NW
	new Vector3(1146.92, 6022.378, 80.34941, "None"), //5 - E
};

for (int i = 2; i <= 5; i++)
{
	if (!WorldQuest.Complete(questId, i))
	{
		if (GoToTask.ToPosition(portsPositions[i]))
		{
			wManager.Wow.Helpers.Lua.LuaDoString("ExtraActionButton1:Click()");
			Thread.Sleep(Usefuls.Latency * 2);
			wManager.Wow.Helpers.ClickOnTerrain.Pulse(portsPositions[i]);
			wManager.Wow.Helpers.Usefuls.WaitIsCasting();
			Thread.Sleep(5 * 1000);
		}
	}
}
return true;

var questId = 42160;
var portsPositions = new List<Vector3>() {
	new Vector3(611.3944, 5525.727, 54.96275, "None"),
	new Vector3(719.3276, 5321.552, 56.6449, "None"),
	new Vector3(586.7864, 5307.248, 45.0335, "None"),
	new Vector3(497.1666, 5311.961, 60.16724, "None"),
};

var progress = WorldQuest.Progress(questId, 1);
if (progress < 0 || progress >= portsPositions.Count)
	return true;

if (GoToTask.ToPosition(portsPositions[progress]))
{
	var portId = 106509;
	var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(portId));
	if (mobEntry != null && mobEntry.IsValid)
	{
		if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) > 15)
		{
			GoToTask.ToPosition(mobEntry.Position);
		}
		wManager.Wow.Helpers.Lua.LuaDoString("ExtraActionButton1:Click()");
		Thread.Sleep(Usefuls.Latency * 2);
		wManager.Wow.Helpers.ClickOnTerrain.Pulse(mobEntry.Position);
		wManager.Wow.Helpers.Usefuls.WaitIsCasting();
		Thread.Sleep(5 * 1000);
		return true;
	}

}
return true;

