var garrisonResources = Questing.Currency(824);
var garrisonResourcesPrice = 2000;
if (garrisonResources < garrisonResourcesPrice)
{
	var cacheID = new List<int>() { /* Garrison Cache */ 236916, 237191, /* Hefty Garrison Cache */ 237723, 237720, /* Full Garrison Cache */ 237724, 237722, };
	var cache = ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(cacheID));
	if (cache != null && cache.IsValid)
	{
		GoToTask.ToPositionAndIntecractWithGameObject(cache.Position, cache.Entry, 1);
		return true;
	}
	uint hugeOgreCacheID = 128313;
	if (ItemsManager.HasItemById(hugeOgreCacheID)) /* Huge Ogre Cache */
	{
		Logging.Write("["+Name+"] You need " + (garrisonResourcesPrice - garrisonResources) + " more Garrison Resources to upgrade Garrison to Level 3, but i have " + ItemsManager.GetNameById(hugeOgreCacheID) + " and going to use it 10 seconds");
		Thread.Sleep(10 * 1000);
		ItemsManager.UseItem(hugeOgreCacheID);
		return true;
	}
	Logging.Write("[" + Name + "] You need " + (garrisonResourcesPrice - garrisonResources) + " more Garrison Resources to upgrade Garrison to Level 3");
	Thread.Sleep(30 * 1000);
	return true;
}
