public class BloodofMyBlood : QuestClass
{
	static int _questID = 41989;
	Vector3 _pos = new Vector3(532.6307, 4316.902, 132.9876);
	Vector3 _end = new Vector3(528.1818, 4252.701, 132.9024, "None");
	List<int> _mobs = new List<int>()
	{
		106239,
		106173,
	};
	bool _followed = false;
	public BloodofMyBlood()
	{
		Name = "Blood of My Blood";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	public override bool Pulse()
	{
		var npc = Questing.FindUnit(_mobs);
		if (npc != null && npc.IsValid)
		{
			if (npc.IsAttackable)
			{
				Questing.Attack(npc);
			}
			else if (!_followed)
			{
				if (GoToTask.ToPositionAndIntecractWithNpc(npc.Position, npc.Entry, 1))
				{
					_followed = true;
				}
			}
			else
			{
				GoToTask.ToPosition(_end);
			}
			return true;
		}
		_followed = false;
		GoToTask.ToPositionAndIntecractWithGameObject(_pos, 249697);
		return true;
	}
	public static void FixCombatStart()
	{
		wManager.Events.FightEvents.OnFightLoop += FightFix;
	}
	public static void FixCombatStop()
	{
		wManager.Events.FightEvents.OnFightLoop -= FightFix;
	}
	static void FightFix(WoWUnit unit, System.ComponentModel.CancelEventArgs cancelable)
	{
		if (unit.IsValid && !unit.IsAttackable)
		{
			cancelable.Cancel = true;
		}
	}
}

