public class HomewardBounding : QuestClass
{
	Vector3 _startPosition = new Vector3(2259.737, 5297.512, 116.0874, "None");// new Vector3(2258.984, 5296.698, 116.086);
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(5 * 60 * 1000);
	int _questID = 41453;
	public HomewardBounding()
	{
		Name = "Homeward Bounding";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1, });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Homeward Bounding] " + text);
	}
	public override bool PickUp()
	{
		Log("timer reset");
		_timer.Reset();
		return base.PickUp();
	}
	public override bool Pulse()
	{
		if (_timer.IsReady)
		{
			Log("abandon");
			Questing.Abandon(_questID);
			Reset();
			return true;
		}
		var kitty = Questing.FindUnit(104478);
		if (kitty != null && kitty.IsValid)
		{
			MountTask.DismountMount();
			Interact.InteractGameObject(kitty.GetBaseAddress, false, false, true);
			Thread.Sleep(Usefuls.Latency);
			Vector3 kLoc = ObjectManager.Me.TargetObject.Position;
			Vector3 mLoc = ObjectManager.Me.Position;

			double diff = 2291.792 - kLoc.X;
			Vector3 nLoc;
			bool right = diff < 0;
			var multiplerX = (diff / 9.0);
			var deltaY = 2.0;
			if (right)
			{
				nLoc = new Vector3(kLoc.X + multiplerX, kLoc.Y - deltaY, kLoc.Z);
				Log("right " + nLoc.ToStringNewVector () );
			}
			else
			{
				nLoc = new Vector3(kLoc.X - multiplerX, kLoc.Y - deltaY, kLoc.Z);
				Log("left " + nLoc.ToStringNewVector());
			}
			if (ObjectManager.Me.Position.DistanceTo(nLoc) > 100)
			{
				Log("too far. skip");
				return true;
			}
			if (GoToTask.ToPosition(nLoc))
			{
				Questing.ClickMove(nLoc, 1f);
			}
			return true;
		}
		Log("to start");
		GoToTask.ToPosition(_startPosition);
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
}

