public class YouveGottoBeKittenMeRightMeow : QuestClass
{
	Vector3 _start = new Vector3(2418.526, 5440.193, 131.7102, "None");
	Vector3 _home = new Vector3(2322.228f, 5470.458f, 118.7071f);
	float _range = 5f;
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(3 * 60 * 1000);
	MemoryRobot.Int128 _lastGuid = MemoryRobot.Int128.Zero();

	public YouveGottoBeKittenMeRightMeow()
	{
		Name = "You've Got to Be Kitten Me Right Meow";
		QuestId.Add(41197);
		Step.AddRange(new[] {7});
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[You've Got to Be Kitten Me Right Meow] " + text);
	}
	public override bool Pulse()
	{
		var kitty = Questing.FindUnit(103511);
		if (kitty != null && kitty.IsValid)
		{
			if (_lastGuid != kitty.Guid)
			{
				_lastGuid = kitty.Guid;
				_timer.Reset();
			}
			else if (_timer.IsReady)
			{
				Log("too long. blacklist");
				wManager.wManagerSetting.AddBlackList(kitty.Guid, 5 * 60 * 1000); //5 min blacklist
				_lastGuid = MemoryRobot.Int128.Zero();
				return true;
			}
			var deltaSummary = new Vector3();
			var delta = kitty.Position - _home;
			var deltaMag = delta.Magnitude();
			var deltaNormalized = delta / deltaMag;
			var deltaNeed = deltaNormalized * (deltaMag + _range);
			var p = _home + deltaNeed;
			if (ObjectManager.Me.Position.DistanceTo(p) > 100)
			{
				Log("too far. skip");
				return true;
			}
			Log("go (" + ObjectManager.Me.Position.DistanceTo(p) +") " + p.ToStringNewVector() );
			if (GoToTask.ToPosition(p))
			{
				GoToTask.ToPosition(kitty.Position);
			}
			return true;
		}
		Log("to start");
		GoToTask.ToPosition(_start);
		return true;
	}
}

