public class BreakingtheSeal : QuestClass
{
	public class PowerSwitch
	{
		public Vector3 position;
		public int Entry;
		public bool isComplete = false;
	}
	List<PowerSwitch> _switches = new List<PowerSwitch>()
	{
		//1
		new PowerSwitch()
		{
			position = new Vector3(2041.389, 5710.63, 86.13902),
			Entry = 250382,
		},
		new PowerSwitch()
		{
			position = new Vector3(2041.752, 5695.899, 85.64749),
			Entry = 250378,
		},
		//2
		new PowerSwitch()
		{
			position = new Vector3(2041.389, 5710.63, 86.13902),
			Entry = 250382,
		},
		new PowerSwitch()
		{
			position = new Vector3(2033.009, 5702.615, 85.02367),
			Entry = 250379,
		},
		new PowerSwitch()
		{
			position = new Vector3(2050.264, 5681.919, 86.05747),
			Entry = 250381,
		},
		new PowerSwitch()
		{
			position = new Vector3(2046.785, 5697.388, 85.847),
			Entry = 250666,
		},
		//3
		new PowerSwitch()
		{
			position = new Vector3(2057.37, 5689.786, 86.30775),
			Entry = 250380,
		},
		new PowerSwitch()
		{
			position = new Vector3(2050.264, 5681.919, 86.05747),
			Entry = 250381,
		},
		new PowerSwitch()
		{
			position = new Vector3(2047.325, 5695.16, 85.81974),
			Entry = 250667,
		},
	};
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(10 * 60 * 1000);
	int _questID = 42225;
	bool _started = false;
	public BreakingtheSeal()
	{
		Name = "Breaking the Seal";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1, 1 });
		_switches.ForEach(ps => ps.isComplete = false);
	}
	public override bool Pulse()
	{
		if (!_started)
		{
			_timer.Reset();
			_started = true;
		}
		if (_timer.IsReady)
		{
			Questing.Abandon(_questID);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 1))
		{
			if (UsePowerSwitch(0))
			{
				if (UsePowerSwitch(1))
				{
				}
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			if (UsePowerSwitch(2))
			{
				if (UsePowerSwitch(3))
				{
					if (UsePowerSwitch(4))
					{
						if (UsePowerSwitch(5))
						{
						}
					}
				}
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			if (UsePowerSwitch(6))
			{
				if (UsePowerSwitch(7))
				{
					if (UsePowerSwitch(8))
					{
					}
				}
			}
			return true;
		}
		return true;
	}
	bool UsePowerSwitch(int i)
	{
		PowerSwitch ps = _switches[i];
		if (ps.isComplete)
			return true;

		Logging.WriteDebug("use power #" + i);
		if (GoToTask.ToPositionAndIntecractWithGameObject(ps.position, ps.Entry))
		{
			Questing.Wait(1);
			ps.isComplete = true;
			return true;
		}
		return false;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1) && Questing.ObjectiveComplete(this, 2) && Questing.ObjectiveComplete(this, 3);
	}
	public override bool PickUp()
	{
		var npc = NpcPickUp;
		if (Questing.Gather(npc.Position, npc.Id, 7))
		{
			Quest.AcceptQuest();
			return true;
		}
		return false;
	}
	public override bool TurnIn()
	{
		var npc = NpcTurnIn;
		if (Questing.Gather(npc.Position, npc.Id, 7))
		{
			Quest.CompleteQuest();
			Questing.Wait(1);
			Questing.CancelCutscene();
			return true;
		}
		return false;
	}

}

