public class StormforgedGrappleLauncher : QuestClass
{
	static int _questID = 39775;
	public StormforgedGrappleLauncher()
	{
		Name = "Stormforged Grapple Launcher";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 0 });
	}
	public override bool HasQuest()
	{
		return !Quest.GetQuestCompleted(_questID);
	}
	public override bool IsComplete()
	{
		return !HasQuest();
	}
	public override bool Pulse()
	{
		var p = new Vector3(3406.307, 3072.334, 526.5183, "None");
		if (GoToTask.ToPosition(p))
		{
			GoToTask.ToPositionAndIntecractWithNpc(p, 107840);
			Quest.CompleteQuest();
		}
		return true;
	}
}

var p = new Vector3(3250.595, 3153.929, 527.5721, "None");
if (GoToTask.ToPosition(p, 20))
{
	MountTask.DismountMount();
	Questing.Gather(p, 240235, 19);
}
return true;
