public sealed class ThoseWhoRememberPickUp : QuestClass
{
	public ThoseWhoRememberPickUp()
	{
		Name = "Those Who Remember (Pick Up)";
		QuestId.Add(37690);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool CanConditions()
	{
		return !Questing.Has(this) || !Questing.Complete(this);
	}
	public override bool HasQuest()
	{
		return CanConditions();
	}
	public override bool IsComplete()
	{
		return Questing.Has(this) || Questing.Complete(this);
	}

	public override bool Pulse()
	{
		var p = new Vector3(672.2411, 6665.096, 59.68708, "None");
		Questing.PickUp(p, 100482, 5);
		return true;
	}
}

//settings
var npcTalk = 88115;
var npcTalkGossipNum = 1;
var npcEscort = 88869;
var posStart = new Vector3(-10.85069, 6734.116, 55.58833, "None");
var attackRange = 30;
var escortRange = 10;

//execute
var npc = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(npcEscort));
if (npc != null && npc.IsValid)
{
	if (npc.InCombat)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitAttackables(attackRange));
		if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Fight.StartFight(mob.Guid);
			return true;
		}
	}
	GoToTask.ToPosition(npc.Position, escortRange);
	return true;
}

var npc0 = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(npcTalk));
if (npc0 != null && npc0.IsValid)
{
	GoToTask.ToPositionAndIntecractWithNpc(posStart, npc0.Entry, npcTalkGossipNum);
	return true;
}

GoToTask.ToPosition(posStart);
return true;

