public sealed class NarthalasAcademy : QuestClass
{
	static Vector3 startPosition = new Vector3(34.43332, 6287.485, 12.76364, "None");
	static Vector3 centerPosition = new Vector3(179.8828, 6341.836, -30.70988, "None");
	static Vector3 entranceTidestoneVault = new Vector3(184.8297, 6325.329, -30.73794, "None");
	static Vector3 entranceHallOfArcaneLearning = new Vector3(172.1961, 6358.398, -30.73846, "None");
	static Vector3 centerTidestoneVault = new Vector3(267.653, 6272.622, -53.77117, "None");
	static Vector3 centerHallOfArcaneLearning = new Vector3(200.9432, 6451.974, -53.77692, "None");

	static int doorHallOfArcaneLearning = 250264;
	static int doorTidestoneVault = 238979;
	static float deltaZ = 0.5f;
	static float roomRadius = 45f;
	static float subzoneRadius = 250f;
	static float centerRaidus = 5;

	public NarthalasAcademy()
	{
		Name = "+Nar'thalas Academy";
		QuestId.Add(37518); //http://www.wowhead.com/quest=37518/the-haunted-halls
		QuestId.Add(42370); //http://www.wowhead.com/quest=42370/wanding-101
		QuestId.Add(42371); //http://www.wowhead.com/quest=42371/study-hall-combat-research
		QuestId.Add(37729); //http://www.wowhead.com/quest=37729/pop-quiz-advanced-rune-drawing
		QuestId.Add(37730); //http://www.wowhead.com/quest=37730/the-headmistress-keys
		QuestId.Add(37469); //http://www.wowhead.com/quest=37469/the-tidestone-shattered
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}

	public static void Log(string text)
	{
		//Logging.Write("[Subzone: Nar'thalas Academy] " + text);
	}

	public override bool HasQuest()
	{
		return Questing.NotCompleteAll(this);
	}

	public override bool IsCompleted()
	{
		return Questing.Complete(this);
	}

	public override bool IsComplete()
	{
		if (IsInCenter)
			return true;

		return false;
	}

	public override bool Pulse()
	{
		if (!IsInCenter)
			ToCenter();

		return true;
	}

	public static bool ToTidestoneVault()
	{
		if (IsInTidestoneVault)
		{
			Log("inside vault path");
			if (!IsInTidestoneVaultRoom)
			{
				Log("inside vault, move path");
				Questing.PathFromNear(pathToTidestoneVault);
				Log("inside vault, move center");
				GoToTask.ToPosition(centerTidestoneVault);
			}
		}
		else if (IsInMiddle)
		{
			Log("in middle. to center");
			ToCenter();
			Log("open door to vault");
			if (GoToTask.ToPositionAndIntecractWithGameObject(ObjectManager.Me.Position, doorTidestoneVault))
			{
				Log("move inside vault");
				GoToTask.ToPosition(pathToTidestoneVault[6]);
				Log("must be in vault path");
			}
		}
		else
		{
			Log("to center of academy");
			ToCenter();
		}
		return true;
	}

	public static bool ToHallOfArcaneLearning()
	{
		if (IsInHallOfArcaneLearning)
		{
			Log("inside hall path");
			if (!IsHallOfArcaneLearningRoom)
			{
				Log("inside hall, move path");
				Questing.PathFromNear(pathToHallOfArcaneLearning);
				Log("inside hall, move center");
				GoToTask.ToPosition(centerHallOfArcaneLearning);
			}
		}
		else if (IsInMiddle)
		{
			Log("in middle. to center");
			ToCenter();
			Log("open door to hall");
			if (GoToTask.ToPositionAndIntecractWithGameObject(ObjectManager.Me.Position, doorHallOfArcaneLearning))
			{
				Log(" move inside hall");
				GoToTask.ToPosition(pathToHallOfArcaneLearning[2]);
			}
		}
		else
		{
			Log("to center of academy");
			ToCenter();
		}
		return true;
	}

	public static void ToOutside()
	{
		if (IsInHallOfArcaneLearning || IsInTidestoneVault)
		{
			Log("to center (in hall or vault)");
			ToCenter();
		}

		if (IsInside)
		{
			Log("move to upper");
			var path = new List<Vector3>(pathInside);
			path.Reverse();
			Questing.PathFromNear(path);
			Log("move outside");
			GoToTask.ToPosition(startPosition);
		}
	}

	public static void ToCenter()
	{
		if (!IsInside)
		{
			Log("not inside. go to start position");
			GoToTask.ToPosition(startPosition);
		}

		if (IsInMiddle || !IsInside)
		{
			Log("in middle or outside. go to center");
			Questing.PathFromNear(pathInside);
			GoToTask.ToPosition(centerPosition);
		}

		if (IsInTidestoneVault)
		{
			Log("in vault. go to center");
			var path = new List<Vector3>(pathToTidestoneVault);
			path.Reverse();
			Questing.PathFromNear(path);
			if (GoToTask.ToPositionAndIntecractWithGameObject(ObjectManager.Me.Position, doorTidestoneVault))
			{
				GoToTask.ToPosition(centerPosition);
			}
		}

		if (IsInHallOfArcaneLearning)
		{
			Log("in hall of acrane learning. to center");
			var path = new List<Vector3>(pathToHallOfArcaneLearning);
			path.Reverse();
			Questing.PathFromNear(path);
			if (GoToTask.ToPositionAndIntecractWithGameObject(ObjectManager.Me.Position, doorHallOfArcaneLearning))
			{
				GoToTask.ToPosition(centerPosition);
			}
		}
		Log("must be in academy center");
		Thread.Sleep(2 * 1000);
	}

	public static bool IsInside
	{
		get
		{
			if (!ObjectManager.Me.IsIndoors)
				return false;

			return ObjectManager.Me.Position.DistanceTo(centerPosition) < subzoneRadius;
		}
	}

	public static bool IsInMiddle
	{

		get
		{
			if (!IsInside)
				return false;

			return ObjectManager.Me.Position.Z > centerPosition.Z - deltaZ;
		}
	}

	public static bool IsInCenter
	{
		get
		{
			if (!IsInside)
				return false;

			return ObjectManager.Me.Position.DistanceTo(centerPosition) < centerRaidus;
		}
	}


	public static bool IsInTidestoneVault
	{
		get
		{
			if (!ObjectManager.Me.IsIndoors)
				return false;

			if (ObjectManager.Me.Position.Z > centerPosition.Z - deltaZ)
				return false;

			return ObjectManager.Me.Position.DistanceTo(entranceTidestoneVault) < ObjectManager.Me.Position.DistanceTo(entranceHallOfArcaneLearning);
		}
	}

	public static bool IsInHallOfArcaneLearning
	{
		get
		{
			if (!ObjectManager.Me.IsIndoors)
				return false;

			if (ObjectManager.Me.Position.Z > centerPosition.Z - deltaZ)
				return false;

			return ObjectManager.Me.Position.DistanceTo(entranceHallOfArcaneLearning) < ObjectManager.Me.Position.DistanceTo(entranceTidestoneVault);
		}
	}

	public static bool IsInTidestoneVaultRoom
	{
		get
		{
			if (!IsInTidestoneVault)
				return false;

			return ObjectManager.Me.Position.DistanceTo(centerTidestoneVault) < roomRadius;
		}
	}

	public static bool IsHallOfArcaneLearningRoom
	{
		get
		{
			if (!IsInHallOfArcaneLearning)
				return false;

			return ObjectManager.Me.Position.DistanceTo(centerHallOfArcaneLearning) < roomRadius;
		}
	}

