//if not in quest area
if (!NarthalasAcademy.IsHallOfArcaneLearningRoom)
{
	NarthalasAcademy.ToHallOfArcaneLearning();
	return true;
}

int dummyId = 107279;
var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(dummyId));
if (mobEntry != null && mobEntry.IsValid)
{
	MovementManager.Face(mobEntry.Position);
	MovementManager.MoveTo(mobEntry.Position);
	Thread.Sleep(Usefuls.Latency * 2);
	MovementManager.StopMove();
	Questing.UseExtraButton();
	Thread.Sleep(2 * 1000);
	wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 3 * 60);
}
return true;

