//if not in quest area
if (!NarthalasAcademy.IsHallOfArcaneLearningRoom)
{
	NarthalasAcademy.ToHallOfArcaneLearning();
	return true;
}

// if victim near
var victims = new List<int>(){
	107301,
	107300,
	107299,
};
var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(victims));
if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
{
	Interact.InteractGameObject(mob.GetBaseAddress);
	Fight.StartFight(mob.Guid);
	return true;
}

if (!Questing.ObjectiveComplete(this, 1))
{
	GoToTask.ToPositionAndIntecractWithGameObject(book1Position, book1);
}
else if (!Questing.ObjectiveComplete(this, 2))
{
	GoToTask.ToPositionAndIntecractWithGameObject(tablePosition, tableId);
}
else if (!Questing.ObjectiveComplete(this, 3))
{
}
else if (!Questing.ObjectiveComplete(this, 4))
{
	var step1 = new Vector3(180.167, 6481.645, -52.86464, "None");
	var step2 = new Vector3(185.017, 6484.542, -51.15879, "None");
	var step3 = new Vector3(186.0768, 6484.852, -49.21067, "None");
	if (GoToTask.ToPosition(step1, 1.5f))
	{
		var bookObj = ObjectManager.GetObjectWoWGameObject().Where(o => o.IsValid && o.Entry == book2).OrderBy(o => o.GetDistance).FirstOrDefault();
		if (bookObj != null)
		{
			Interact.InteractGameObject(bookObj.GetBaseAddress);
			Thread.Sleep(3 * 1000);
		}
	}
}
else if (!Questing.ObjectiveComplete(this, 5))
{
	GoToTask.ToPositionAndIntecractWithGameObject(tablePosition, tableId);
}
else if (!Questing.ObjectiveComplete(this, 6))
{
}
else if (!Questing.ObjectiveComplete(this, 7))
{
	GoToTask.ToPositionAndIntecractWithGameObject(book3Position, book3);
}
else if (!Questing.ObjectiveComplete(this, 8))
{
	GoToTask.ToPositionAndIntecractWithGameObject(tablePosition, tableId);
}
else if (!Questing.ObjectiveComplete(this, 9))
{
}
return true;

