#if VISUAL_STUDIO
using robotManager.Helpful;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using wManager.Wow.Bot.Tasks;
using wManager.Wow.Class;
using wManager.Wow.Helpers;
using wManager.Wow.ObjectManager;
using wManager.Wow.Enums;
#endif

public class SaveYourself : QuestClass
{
	Vector3 _farondisPos = new Vector3(-101.928, 6021.24, 0.493063, "None");
	int _farondisId = 89009;

	float _attackDist = 35;
	uint _spellFireball = 178784;
	uint _spellMeteorStorm = 179215;
	uint _spellBlink = 225948;

	public SaveYourself()
	{
		Name = "Save Yourself";
		QuestId.Add(37530);
		Step.AddRange(new[] { 1, 1, 1, 1, 1, 1});
	}

	static void Log(string text)
	{
		//Logging.Write("[Save Yourself] " + text);
	}

	public override bool IsComplete()
	{
		return (Questing.ObjectiveComplete(this, 7) && ObjectManager.Me.PlayerUsingVehicle) || Questing.Complete(this);
	}

	public override bool Pulse()
	{
		if (TryMountFarondis())
			return true;

		if (TryCombat())
			return true;

		if (!Questing.ObjectiveComplete(this, 2))
		{
			var p = new Vector3(-3.882, 6276.46, 3.146761);
			GoToTask.ToPosition(p);
			return true;
		}
		else if (!Questing.ObjectiveComplete(this, 3) || !Questing.ObjectiveComplete(this, 4))
		{
			var mobs = new List<int>() {
				89116,
			};
			List<Vector3> path = new List<Vector3>() {
				new Vector3(-26.50426, 6178.747, 4.55294),
			};
			TryFind(mobs, path);
			return true;
		}
		else if (!Questing.ObjectiveComplete(this, 5))
		{
			var p = new Vector3(-11.47015, 5977.641, 0.7397661);
			GoToTask.ToPosition(p);
			return true;
		}
		else if (!Questing.ObjectiveComplete(this, 6))
		{
			//do nothing
		}
		else if (!Questing.ObjectiveComplete(this, 7))
		{
			var p = new Vector3(-98.56056, 6020.948, 0.3888235);
			GoToTask.ToPosition(p);
			return true;
		}
		return true;
	}

	bool TryCombat()
	{
		var target = ObjectManager.GetUnitAttackPlayer().FirstOrDefault();
		if (target == null)
			target = ObjectManager.Me.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;


		Interact.InteractGameObject(target.GetBaseAddress);
		var dist = target.GetDistance;
		if (dist > _attackDist)
		{
			var result = false;
			var path = PathFinder.FindPath(target.Position, out result);
			if (result)
				MovementManager.Go(path);

			Thread.Sleep(1 * 1000);
			return true;
		}

		MovementManager.StopMove();

		if (!ObjectManager.Me.IsFacing(target.Position, 0.5f))
		{
			MovementManager.Face(target);
			//Move.Backward(Move.MoveAction.DownKey, Others.Random(300, 500));
			//Thread.Sleep(Others.Random(200, 400));
		}

		if (Questing.NoCooldownSpell(_spellMeteorStorm))
		{
			Questing.VehicleButton(2);
			return true;
		}
		if (Questing.NoCooldownSpell(_spellFireball))
		{
			Questing.VehicleButton(1);
			return true;
		}
		return false;
	}

	bool TryFind(List<int> mobs, List<Vector3> path)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Thread.Sleep(Usefuls.Latency + 150);
			return true;
		}
		if (!MovementManager.InMovementLoop)
		{
			var closest = path.OrderBy(v => ObjectManager.Me.Position.DistanceTo(v)).FirstOrDefault();
			GoToTask.ToPosition(closest);
			MovementManager.GoLoop(path);
			return true;
		}
		return true;
	}

	bool TryMountFarondis()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
			return false;

		RestoreSettings();
		if (GoToTask.ToPositionAndIntecractWithNpc(_farondisPos, _farondisId, 1))
		{
			ChangeSettings();
			Thread.Sleep(Others.Random(1500, 2500));
		}
		return true;
	}

	public static void ChangeSettings()
	{
		Log("change settings");
		Conditions.ForceIgnoreIsAttacked = true;
		wManager.wManagerSetting.CurrentSetting.UseMount = false;
	}

	public static void RestoreSettings()
	{
		Log("restore settings");
		Conditions.ForceIgnoreIsAttacked = false;
		wManager.wManagerSetting.CurrentSetting.UseMount = true;
	}

}
