public class YouLiftBrul : QuestClass
{
	int _questID = 39440;
	public YouLiftBrul()
	{
		Name = "You Lift, Brul?";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1 });
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.HaveBuff(189912))
		{
			if (Questing.Abandon(_questID))
			{
				Logging.Write("Lose quest buff, abandon quest " + Name);
				Thread.Sleep(Others.Random(1000, 2000));
			}
			return true;
		}
		var p0 = new Vector3(5838.018, 4518.045, 612.2607);
		var p1 = new Vector3(5873.235, 4516.495, 612.2515);
		var p2 = new Vector3(5838.502, 4517.966, 612.261);
		if (!Questing.ObjectiveComplete(this, 1))
		{
			GoToTask.ToPosition(p1);
			return true;
		}
		GoToTask.ToPosition(p2);
		return true;
	}
}

