public class BolasBastion : QuestClass
{
	List<int> _mobs = new List<int>()
	{
		108060,
		108052,
	};
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(6 * 1000);
	public BolasBastion()
	{
		Name = "Bolas Bastion";
		QuestId.Add(42630);
		Step.AddRange(new[] { 42 });
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			var p = new Vector3(4160.632, 4395.381, 768.3291, "None");
			GoToTask.ToPositionAndIntecractWithNpc(p, 107988);
			return true;
		}
		Conditions.ForceIgnoreIsAttacked = true;
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			for (int i = 0; i < 5; i++)
			{
				if (mob == null || !mob.IsValid || mob.IsDead)
				{
					wManager.wManagerSetting.AddBlackList(mob.Guid, 1 * 60 * 1000);
					break;
				}
				ClickToMove.CGPlayer_C__ClickToMove(mob.Position.X, mob.Position.Y, mob.Position.Z, mob.Guid, 1, 0.5f);
				Thread.Sleep(Usefuls.Latency);
				if (_timer.IsReady)
				{
					Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton2");
					_timer.Reset();
					Thread.Sleep(Others.Random(300, 500));
					return true;
				}
				Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
				Thread.Sleep(Others.Random(1000, 1200));
				return true;
			}
		}
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
	public override bool TurnIn()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
		{
			Usefuls.EjectVehicle();
		}
		return base.TurnIn();
	}
}

