public class MountainstriderRoundUp : QuestClass
{
	List<int> _mobs = new List<int>()
	{
		107680,
		116315,
	};
	List<Vector3> _hotspots = new List<Vector3>()
	{
		new Vector3(4088.927, 4247.219, 752.7471),
		new Vector3(4015.575, 4266.288, 786.8982),
		new Vector3(3980.571, 4194.148, 823.0579),
		new Vector3(3962.082, 4149.36, 858.9806),
	};
	Vector3 _home = new Vector3(4113.937, 4291.494, 768.3278);
	public MountainstriderRoundUp()
	{
		Name = "Mountainstrider Round-Up";
		QuestId.Add(42596);
		Step.AddRange(new[] { 3 });
	}
	public override bool Pulse()
	{
		Conditions.ForceIgnoreIsAttacked = true;
		if (ObjectManager.Me.HaveBuff(214322))
		{
			GoToTask.ToPosition(_home);
			return true;
		}
		var strider = Questing.FindUnit(_mobs);
		if (strider != null && strider.IsValid)
		{
			if (strider.GetDistance > 5)
			{
				GoToTask.ToPosition(strider.Position);
				return true;
			}
			Questing.FightclassDisabled = true;
			MountTask.DismountMount();
			Questing.DisableForm();
			GoToTask.ToPositionAndIntecractWithNpc(strider.Position, strider.Entry);
			Questing.FightclassDisabled = false;
			return true;
		}
		var p = _hotspots[Others.Random(0, _hotspots.Count - 1)];
		GoToTask.ToPosition(p, 5f, false, (c) => {
			var s = Questing.FindUnit(_mobs);
			if (s != null && s.IsValid)
				return false;

			return true;
		});
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
}
