public class SuppliesNeededAstralGloryTurnIn : QuestClass
{
	int _questID = 48338;
	public SuppliesNeededAstralGloryTurnIn()
	{
		Name = "Supplies Needed: Astral Glory (Turn In)";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 40 });
	}
	public override bool CanConditions()
	{
		var result = WorldQuest.Complete(_questID, 1);
		//WOW bugged. quest marked as complete and allown turn-in, but quest objective is "0/1"
		if (!result)
		{
			result = ItemsManager.GetItemCountByIdLUA(151565) >= 40;
		}
		return result;
	}
	public override bool HasQuest()
	{
		return CanConditions();
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(_questID);
	}
	public override bool IsCompleted()
	{
		return !CanConditions();
	}
	public override bool Pulse()
	{
		return TurnIn();
	}
}

