public class SupplyingKrokuun : QuestClass
{
	public SupplyingKrokuun()
	{
		Name = "Supplying Krokuun";
		QuestId.Add(48910);
		Step.AddRange(new[] { 150 });
	}
	public override bool IsComplete()
	{
		return ItemsManager.GetItemCountByIdLUA(152095) < 5 && LegionQuests.OrderHallResources >= 150;
	}
	public override bool HasQuest()
	{
		return Quest.GetQuestCompleted(48442) && !Quest.GetQuestCompleted(QuestId);
	}
}

