public class WhereTheyLeastExpectIt : QuestClass
{
	public WhereTheyLeastExpectIt()
	{
		Name = "Where They Least Expect It";
		QuestId.Add(48461);
		Step.AddRange(new[] { 1 });
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name+"] " + text);
	}
	public override bool Pulse()
	{
		if (ArgusHelper.Invasion.Work())
			return true;
		if (ArgusHelper.Invasion.Portals.UseNear())
			return true;

		var portals = ArgusHelper.Invasion.Portals.GetMacAree();
		//mac'aree
		if (portals.Count > 0)
		{
			Log("found invasions in Mac'Aree=" + portals.Count);
			if (!ArgusHelper.InMacAreeOpen)
			{
				ArgusHelper.ToMacAreeOpen();
				return true;
			}
			var p = portals.OrderBy(v => v.DistanceTo2D(ObjectManager.Me.Position)).FirstOrDefault();
			Log("go to portal at=" + p.ToStringNewVector());
			GoToTask.ToPosition(p);
			return true;
		}
		//krokuun
		portals = ArgusHelper.Invasion.Portals.GetKrokuun();
		if (portals.Count > 0)
		{
			Log("found invasions in Krokuun=" + portals.Count);
			if (!ArgusHelper.InKrokuunOpen)
			{
				ArgusHelper.ToKrokuunOpen();
				return true;
			}
			var p = portals.OrderBy(v => v.DistanceTo2D(ObjectManager.Me.Position)).FirstOrDefault();
			Log("go to portal at=" + p.ToStringNewVector());
			GoToTask.ToPosition(p);
			return true;
		}
		//antoran wastes
		portals = ArgusHelper.Invasion.Portals.GetAntoranWastes();
		if (portals.Count > 0)
		{
			Log("found invasions in Krokuun=" + portals.Count);
			if (!ArgusHelper.InAntoranWastesOpen)
			{
				ArgusHelper.ToAntoranWastesOpen();
				return true;
			}
			var p = portals.OrderBy(v => v.DistanceTo2D(ObjectManager.Me.Position)).FirstOrDefault();
			Log("go to portal at=" + p.ToStringNewVector());
			GoToTask.ToPosition(p);
			return true;
		}
		Log("no invasions found in Argus");
		Questing.Wait(60);
		return true;
	}
	public override bool IsComplete()
	{
		if (ArgusHelper.Invasion.InRift)
			return false;

		return Questing.ObjectiveComplete(this, 1);
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.TurnIn();
	}
}

